package DEPackage;

public class LoopAssignment2of2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Question 2 of 2: Create another program using only While loop.
		
	
	    	
	        int number = 4;
	        long fact = 1;
	        int n = 1;
	        while(n<=number)
	        {
	            fact = fact * n;
	            n++;
	        }
	        System.out.println("Factorial of "+number+" is: "+fact);
	    }
	
}
